// frontend/src/integrations/hubspot.js
const HubSpotIntegration = {
    id: "hubspot",
    title: "HubSpot",
    description: "Connect to HubSpot and load contacts, companies, and deals",
    
    authorize: (user, org) => {
      const formData = new FormData();
      formData.append('user_id', user);
      formData.append('org_id', org);
      
      return fetch('/integrations/hubspot/authorize', {
        method: 'POST',
        body: formData
      })
      .then(res => {
        if (!res.ok) throw new Error('Authorization failed');
        return res.json();
      })
      .then(data => {
        window.open(data.url || '/integrations/hubspot/authorize', '_blank');
        return { type: 'hubspot' };
      });
    },
  
    fetchItems: async (credentials) => {
      const formData = new FormData();
      formData.append('credentials', JSON.stringify(credentials));
      
      const res = await fetch('/integrations/hubspot/get_hubspot_items', {
        method: 'POST',
        body: formData
      });
      
      if (!res.ok) {
        const txt = await res.text();
        throw new Error("Failed loading HubSpot items: " + txt);
      }
      
      return await res.json();
    },
  
    getCredentials: async (user, org) => {
      const formData = new FormData();
      formData.append('user_id', user);
      formData.append('org_id', org);
      
      const res = await fetch('/integrations/hubspot/credentials', {
        method: 'POST',
        body: formData
      });
      
      if (!res.ok) throw new Error('Failed to get credentials');
      return await res.json();
    }
  };
  
  export default HubSpotIntegration;
