// hubspot.js
import axios from 'axios';

const API = "/api/integrations/hubspot/";

export async function getAuthorizationUrl(userId) {
  const response = await axios.get(`${API}authorize`, { params: { user_id: userId } });
  return response.data.auth_url;
}

export async function completeOAuthCallback(code, state, userId) {
  const response = await axios.get(
    `${API}callback?code=${encodeURIComponent(code)}&state=${encodeURIComponent(state)}`,
    { params: { user_id: userId } }
  );
  return response.data;
}

export async function fetchItems(userId) {
  const response = await axios.get(`${API}items`, { params: { user_id: userId } });
  return response.data;
}
