const HUBSPOT = {
    id: "hubspot",
    name: "HubSpot",
    authorize: async (userId = "u1", orgId = "o1") => {
      const resp = await fetch(`/integrations/hubspot/authorize?user_id=${encodeURIComponent(userId)}&org_id=${encodeURIComponent(orgId)}`);
      const { url } = await resp.json();
      window.location.href = url;
    },
    getItems: async (userId = "u1", orgId = "o1") => {
      const resp = await fetch(`/integrations/hubspot/items?user_id=${encodeURIComponent(userId)}&org_id=${encodeURIComponent(orgId)}`);
      if (!resp.ok) throw new Error("Failed to fetch items");
      return await resp.json();
    },
  };
  
  export default HUBSPOT;
