// frontend/src/integrations/hubspot.js
export const HUBSPOT_INTEGRATION = {
    id: "hubspot",
    name: "HubSpot",
    description: "Connect to HubSpot to load contacts and companies.",
    authorize: async function () {
      // Open the backend authorize endpoint which will redirect to HubSpot.
      // Replace the path if your backend route differs.
      const authUrl = "/integrations/authorize/hubspot";
      // Open in a new window so the redirect does not replace the SPA.
      window.open(authUrl, "_blank", "noopener,noreferrer");
    },
    getItems: async function () {
      // Call your backend to fetch items for UI listing
      // backend route: /integrations/hubspot/items or similar - create it if not present
      const res = await fetch("/integrations/hubspot/items");
      if (!res.ok) throw new Error("Failed to fetch HubSpot items");
      const data = await res.json();
      // return or map into the shape your UI expects (IntegrationItem)
      return data;
    }
  };
